import { DataBase } from './DataBase';

export default class DataMgcConfig extends DataBase {
  protected constructor() {
    super();
  }

  private static _instance: DataMgcConfig;

  static getInstance(): DataMgcConfig {
    if (!this._instance) {
      this._instance = new DataMgcConfig();
    }
    return this._instance;
  }

  getShare(): any {
    return this._data.share;
  }

  getMinimumGameVersion(): any {
    return this._data.minimumGameVerison;
  }
}
