enum LoginType {
  //自动登录
  auto = 1,
  //SDK登录
  sdk
}

interface LoginSDKInterface {
  /**
   * sdk登录入口
   * @param params 登录类型 参见LoginType
   * @param callback 成功或者失败会回调这个方法
   * @param options 扩展选项
   */
  login(
    loginType: LoginType,
    callback: (isSuccess: boolean, data: any) => void,
    options?: {
      forceLogin?: boolean;
    }
  );
}

export { LoginSDKInterface, LoginType };
