import RainbowConfig from '../rainbow/RainbowConfig';
import Singleton from './Singleton';
/**
 * 日志回捞
 * 包装Logan
 */
export default class LoganUtil extends Singleton {
    /**
     * 上报log
     * @param content log内容
     */
    log(content) {
        let game = RainbowConfig.gameName;
        let logString = `[${game}] ${content}`;
        if (!process.env.minigame) {
            window['Logan'] && window['Logan'].log(logString, 'mtgame');
        }
    }
}
