/**
 * 判断两个数据内部的所有值是否相等
 * @param dataA 数据A
 * @param dataB 数据B
 */
function equal(dataA, dataB) {
    var result = false, keysA, keysB;
    if (dataA === dataB) {
        return true;
    }
    if (typeof dataA !== typeof dataB) {
        return false;
    }
    if (dataB === null) {
        return false;
    }
    if (typeof dataA === 'object') {
        if (dataA.constructor === dataB.constructor) {
            if (dataA instanceof Array) {
                if (dataA.length === dataB.length) {
                    return dataA.every((element, i) => {
                        return equal(element, dataB[i]);
                    });
                }
                else {
                    return false;
                }
            }
            else if (dataA instanceof RegExp) {
                return dataA.toString() === dataB.toString();
            }
            else {
                keysA = Object.keys(dataA);
                keysB = Object.keys(dataB);
                if (keysA.length === keysB.length) {
                    return keysA.every(element => {
                        return equal(dataA[element], dataB[element]);
                    });
                }
                else {
                    return false;
                }
            }
        }
        else {
            return false;
        }
    }
    else if (typeof dataA === 'function') {
        return dataA.toString() === dataB.toString();
    }
    return result;
}
export { equal };
