import BottomMenuView from './BottomMenuView';
/**
 * 底部菜单父类
 */
export default class BottomMenu extends Laya.View {
    private menuView;
    private loganName;
    private _isCloseOnSide;
    private _closeOnSideClickListener;
    constructor();
    /**
     * logan日志上报名字
     * 一般使用类名
     * 需要子类在构造方法中调用
     * @param loganName
     */
    setLoganName(loganName: string): void;
    /**
     * 设置是否响应点击底部菜单外部区域关闭
     * @param isCloseOnSide 是否影响
     */
    setCloseOnSide(isCloseOnSide: boolean): void;
    /**
     * 返回是否响应点击底部菜单外部区域关闭
     */
    isCloseOnSide(): boolean;
    /**
     * 设置点击底部菜单外部区域执行方法
     * @param closeOnSideClickListener 点击弹框外部区域执行方法
     */
    setCloseOnSideClickListener(closeOnSideClickListener: Function): void;
    /**
     * 返回已设置点击底部菜单外部区域执行方法
     */
    getCloseOnSideClickListener(): Function;
    /**
     * 关闭底部菜单
     */
    close(): void;
    /**
     * [游戏框架内部调用]
     * 设置menuView
     * @param menuView
     */
    setMenuView(menuView: BottomMenuView): void;
    onOpened(): void;
    onClosed(): void;
}
