/**
 * 对话框父类
 */
export default class DialogBase extends Laya.Dialog {
    private _isCloseOnSide;
    private loganName;
    private touchBlockLayer;
    private _closeOnSideClickListener;
    private _isFullScreen;
    private _isFullScreenExit;
    private _isEffectEnabled;
    private curShowEffect;
    private curShowEffectUserSet;
    private curCloseEffect;
    private curCloseEffectUserSet;
    private maskBgAlpha;
    constructor();
    /**
     * logan日志上报名字
     * 一般使用类名
     * 需要子类在构造方法中调用
     * @param loganName
     */
    setLoganName(loganName: string): void;
    /**
     * 设置是否响应点击弹框外部区域
     * @param isCloseOnSide 是否影响
     */
    setCloseOnSide(isCloseOnSide: boolean): void;
    /**
     * 设置点击弹框外部区域执行方法
     * @param closeOnSideClickListener 点击弹框外部区域执行方法
     */
    setCloseOnSideClickListener(closeOnSideClickListener: Function): void;
    /**
     * 返回点击弹框外部区域执行方法
     * @returns
     */
    getCloseOnSideClickListener(): Function;
    /**
     * 设置对话框显示效果
     * @param showEffect 显示效果
     */
    setShowEffect(showEffect?: Laya.Handler): void;
    /**
     * 设置对话框关闭效果
     * @param closeEffect 关闭效果
     */
    setCloseEffect(closeEffect?: Laya.Handler): void;
    /**
     * 设置成全屏弹框，全屏弹框相比一般弹框有很大性能优化
     *
     */
    setFullScreen(): void;
    /**
     * 是否响应点击弹框外部区域
     */
    isCloseOnSide(): boolean;
    /**
     * 设置蒙层透明度
     * @param alpha
     */
    setMaskBgAlpha(alpha: any): void;
    /**
     * 返回背景透明度
     * @returns
     */
    getMaskBgAlpha(): number;
    /**
     * 设置是否启用通用弹出关闭效果
     * @param effectEnabled 是否启用通用弹出关闭效果
     */
    setEffectEnabled(effectEnabled: any): void;
    /**
     * 关闭弹框
     * @param type
     */
    close(type?: string): void;
    _open(modal: boolean, closeOther: boolean, showEffect: boolean): void;
    onOpened(param: any): void;
    onClosed(): void;
    private fullScreenExit;
}
