import TouchBlockLayer from './TouchBlockLayer';
import BottomMenu from './BottomMenu';
import Singleton from '../util/Singleton';
/**
 * GUI管理器
 * 处理其他GUI的弹出关闭逻辑
 */
export default class GUIManager extends Singleton {
    private touchBlockLayer;
    private globalCloseBtn;
    private bottomMenu;
    /**
     * 显示全局的触摸屏蔽层
     * @param isGray
     */
    showTouchBlockLayer(isGray?: boolean): void;
    /**
     * 返回全局的触摸屏蔽层
     */
    getTouchBlockLayer(): TouchBlockLayer;
    /**
     * 移除全局的触摸屏蔽层
     */
    closeTouchBlockLayer(): void;
    /**
     * 显示底部菜单
     * @param bottomMenu 底部菜单
     */
    showBottomMenu(bottomMenu: BottomMenu): void;
    /**
     * 返回底部菜单
     */
    getBottomMenu(): BottomMenu;
    /**
     * 关闭底部菜单
     */
    closeBottomMenu(): void;
    /**
     * 显示全局关闭按钮
     */
    showGlobalCloseBtn(globalCloseBtnParam?: {
        visible?: boolean;
        right?: number;
        y?: number;
        skin?: string;
    }): void;
    /**
     * 返回全局关闭按钮
     * 只有web渠道可以返回
     */
    getGlobalCloseBtn(): Laya.Image;
}
