import HttpRequest from '../network/HttpRequest';
/**
 * 资源加载器
 */
export default class ResourceLoader {
    private static bitmapFontMap;
    /**
     * 并行加载，不分先后顺序
     * 支持加载任何资源 包括request
     * @param arr 资源数组 （或者request）
     * @param caller 执行域名
     * @param callback 加载回调
     */
    static load(arr: (string | HttpRequest | Promise<any>)[], caller: any, callback: Function): void;
    static loadHttpRequest(request: HttpRequest): Promise<any>;
    static loadFont(fontPath: string): Promise<any>;
    static removeFont(fontPath: string): void;
    private static loadCommon;
}
