/**
 * 界面父类
 */
export default class SceneBase extends Laya.Scene {
    private loganName;
    constructor();
    /**
     * logan日志上报名字
     * 一般使用类名
     * 需要子类在构造方法中调用
     * @param loganName
     */
    setLoganName(loganName: string): void;
    /**
     * 适配背景
     * 适配后背景会自动按比例拉伸扩充到全屏
     * @param bg
     */
    adapterBg(bg: Laya.Sprite, option?: {
        bgWidth: number;
        bgHeight: number;
    }): void;
    /**
     * 适配刘海
     * 适配后scene的高度会排除刘海的高度*2 垂直居中， scene的bottom和top不再是屏幕的最下或者最上，而是空出了刘海位置
     *
     */
    adapterNotch(): void;
    onOpened(): void;
    doClose(): void;
    onClosed(): void;
}
