import Singleton from './Singleton';
/**
 * 设备处理类
 */
export default class DeviceUtil extends Singleton {
    private notchLength;
    /**
     * 是否有刘海
     * 长宽比>1.99的手机都认为有刘海
     */
    hasNotch(): boolean;
    /**
     * 设置刘海高度
     * @param notchLength 刘海高度，默认为60
     */
    setDefaultNotchLength(notchLength: number): void;
    /**
     * 返回刘海高度
     */
    getNotchLength(): number;
    private getNotchLengthWidthRate;
}
