import Singleton from './Singleton';
/**
 * 日志管理类
 * info, warn, error方法会在任何环境都会输出
 * log, color 方法在非线上环境或者 调试模式下会输出
 */
export default class LoggerUtil extends Singleton {
    private env;
    private devEnabled;
    /**
     * 设置环境
     * @param env test或者production
     */
    setEnv(env: string): void;
    /**
     * 开启调试模式
     * @param env test或者production
     */
    setDevEnabled(isDevEnabled: boolean): void;
    /**
     * 输出普通日志
     * 线上环境 非调试模式 不输出
     * @param args 日志数组
     */
    log(...args: any[]): void;
    /**
     * 输出信息日志
     * @param args 日志数组
     */
    info(...args: any[]): void;
    /**
     * 输出警告日志
     * @param args 日志数组
     */
    warn(...args: any[]): void;
    /**
     * 输出错误日志
     * @param args 日志数组
     */
    error(...args: any[]): void;
    /**
     * 输出带颜色的日志
     * @param args 日志数组
     */
    colorInfo(color: 'red' | 'green' | 'blue' | 'orange' | 'violet', ...args: any[]): void;
    /**
     * 输出带颜色的日志
     * 线上环境不输出
     * @param args 日志数组
     */
    color(color: 'red' | 'green' | 'blue' | 'orange' | 'violet', ...args: any[]): void;
    private executeConsole;
    private getString;
}
