import Singleton from './Singleton';
/**
 * url处理类
 */
export default class UrlUtil extends Singleton {
    /**
     * 将url拼接参数后返回新的url
     * @param url 原始url
     * @param queryObject 参数Object
     */
    getUrlAppendQueryObject(url: string, queryObject?: Record<string, any>): string;
    /**
     * 将url转成imeituan格式
     * @param url 网址
     * @param query imeituan 扩展参数格式{notitlebar,wkwebview,singleton}
     */
    getImeituanUrl(url: string, query?: {
        notitlebar?: 1 | 0;
        wkwebview?: 1;
        singleton?: number;
    }, imeituanUrlPrefix?: string): any;
    /**
     * 如果url是imeituan格式则返回原始网址
     * @param url 网址
     */
    getUrlIfImeituan(url: string, imeituanUrlPrefix?: string): string;
}
